CREATE TABLE "tigers" ("id" character varying NOT NULL, "create_at" timestamptz default current_timestamp NOT NULL, "update_at" timestamptz default current_timestamp NOT NULL, "name" character varying NOT NULL, "date_of_birth" timestamp(0)with time zone NOT NULL, "description" character varying NULL DEFAULT '', "longitude" double precision NOT NULL, "latitude" double precision NOT NULL, "last_seen" timestamp(0)with time zone NOT NULL, "age" bigint NOT NULL, "deleted" boolean NOT NULL DEFAULT false, PRIMARY KEY ("id"));
CREATE TABLE "sightings" ("id" character varying NOT NULL, "create_at" timestamptz default current_timestamp NOT NULL, "update_at" timestamptz default current_timestamp NOT NULL, "description" character varying NULL DEFAULT '', "longitude" double precision NOT NULL, "latitude" double precision NOT NULL, "seen_at" timestamp(0)with time zone NOT NULL, "tiger_id" character varying NOT NULL, PRIMARY KEY ("id"), CONSTRAINT "sightings_tigers_sightings" FOREIGN KEY ("tiger_id") REFERENCES "tigers" ("id") ON DELETE NO ACTION);
CREATE TABLE "images" ("id" character varying NOT NULL, "create_at" timestamptz default current_timestamp NOT NULL, "update_at" timestamptz default current_timestamp NOT NULL, "uri" character varying NOT NULL, "upload_status" character varying NOT NULL, "sighting_id" character varying NOT NULL, PRIMARY KEY ("id"), CONSTRAINT "images_sightings_images" FOREIGN KEY ("sighting_id") REFERENCES "sightings" ("id") ON DELETE NO ACTION);
